	(2000000398, 'The decomposition of the planet and its living organisms into base elements: metals, rocks, gases. This is followed by a period of reconstitution of each element into the original planetary blueprint.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000390, 'Hear that lads? It\'s only the end of the world! Well, what are ye waiting for, ye rusty machine? Initiate Reply-code Alpha, all is well! Pints are on me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000399, 'Request denied. Reply-codes built into Algalon observer entity. He is both messenger and message.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000391, 'Well, what\'s this Algalon going to be looking for with his diagnostics? What are our chances?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000400, 'Algalon diagnostics assess danger of systemic Old God corruption in planetary vital functions. Calculating chance of Omega Reply-code...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000401, 'Ninety-nine point nine nine percent.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000402, 'That\'s repeating of course.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000392, 'Blast it, lads. Looks like we\'ve got a fight ahead of us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
	(2000000363, 'Commencing Watcher Freya status analysis.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
